import SwiftUIstruct OnboardingMentalHealthGoalsView: View {    @ObservedObject var coreDataViewModel: CoreDataViewModel        // State to track navigation    @State private var navigateToCustomGoals = false    var body: some View {        NavigationStack {            VStack(spacing: 20) {                // Header Text                Text("Select Your Mental Health Goals")                    .font(.title)                    .fontWeight(.bold)                    .padding(.top, 100)                    .foregroundColor(.white)                // Mental Health Goals Toggles                if let mentalHealthEntity = coreDataViewModel.mentalHealthEntity {                    Toggle("Track Meditation", isOn: Binding(                        get: { mentalHealthEntity.isMeditationTracked },                        set: { coreDataViewModel.updateMentalHealthGoal(for: \.isMeditationTracked, value: $0) }                    ))                    .padding()                    .background(Color.white.opacity(0.8))                    .cornerRadius(12)                    .padding(.horizontal, 20)                                        Toggle("Track Outdoor Time", isOn: Binding(                        get: { mentalHealthEntity.isOutdoorTimeTracked },                        set: { coreDataViewModel.updateMentalHealthGoal(for: \.isOutdoorTimeTracked, value: $0) }                    ))                    .padding()                    .background(Color.white.opacity(0.8))                    .cornerRadius(12)                    .padding(.horizontal, 20)                } else {                    Text("Loading mental health goals...")                        .foregroundColor(.white)                }                Spacer()                                // Next Button                Button(action: {                    navigateToCustomGoals = true                }) {                    Text("Next")                        .font(.headline)                        .foregroundColor(.white)                        .padding()                        .frame(maxWidth: .infinity)                        .background(Color.purple)                        .cornerRadius(15)                        .padding(.horizontal, 20)                }                .navigationDestination(isPresented: $navigateToCustomGoals) {                    OnboardingCustomGoalsView(coreDataViewModel: coreDataViewModel)                }                Spacer()            }            .padding()            .background(                Image("background_image")                    .resizable()                    .scaledToFill()                    .edgesIgnoringSafeArea(.all)            )        }    }}#Preview {    OnboardingMentalHealthGoalsView(coreDataViewModel: CoreDataViewModel())}