import SwiftUIstruct OnboardingMentalHealthGoalsView: View {    @ObservedObject var coreDataViewModel: CoreDataViewModel        // State to track navigation    @State private var navigateToCustomGoals = false    var body: some View {        NavigationStack {            VStack(spacing: 20) {                // Header Text                Text("Select Your Mental Health Goals")                    .font(.largeTitle)                    .fontWeight(.bold)                    .padding(.top, 50)                    .foregroundColor(.white)                    .shadow(color: .black.opacity(0.8), radius: 1, x: 0, y: 1)                // Mental Health Goals Toggles                if let mentalHealthEntity = coreDataViewModel.mentalHealthEntity {                    mentalHealthGoalRow(title: "Track Meditation", isOn: Binding(                        get: { mentalHealthEntity.isMeditationTracked },                        set: { coreDataViewModel.updateMentalHealthGoal(for: \.isMeditationTracked, value: $0) }                    ))                                        mentalHealthGoalRow(title: "Track Outdoor Time", isOn: Binding(                        get: { mentalHealthEntity.isOutdoorTimeTracked },                        set: { coreDataViewModel.updateMentalHealthGoal(for: \.isOutdoorTimeTracked, value: $0) }                    ))                } else {                    Text("Loading mental health goals...")                        .foregroundColor(.white)                        .shadow(color: .black.opacity(0.8), radius: 1, x: 0, y: 1)                }                Spacer()                                // Next Button                Button(action: {                    navigateToCustomGoals = true                }) {                    Text("Next")                        .font(.headline)                        .foregroundColor(.white)                        .padding()                        .frame(maxWidth: .infinity)                        .background(Color.purple)                        .cornerRadius(15)                        .padding(.horizontal, 20)                        .shadow(color: .black.opacity(0.8), radius: 1, x: 0, y: 1)                }                .navigationDestination(isPresented: $navigateToCustomGoals) {                    OnboardingCustomGoalsView(coreDataViewModel: coreDataViewModel)                }                Spacer()            }            .padding()            .background(                Image("background_image")                    .resizable()                    .scaledToFill()                    .edgesIgnoringSafeArea(.all)            )        }    }    // Helper function to create mental health goal rows with consistent height    private func mentalHealthGoalRow(title: String, isOn: Binding<Bool>) -> some View {        HStack {            Text(title)                .foregroundColor(.white)                .padding(.vertical, 10)                .padding(.leading, 15)                .shadow(color: .black.opacity(0.8), radius: 1, x: 0, y: 1)            Spacer()            Toggle("", isOn: isOn)                .toggleStyle(SwitchToggleStyle(tint: .purple))                .padding(.trailing, 15)        }        .padding(.horizontal)        .frame(maxWidth: .infinity, minHeight: 50) // Set a fixed minimum height for consistency        .background(Color.white.opacity(0.2))        .cornerRadius(10)        .padding(.horizontal, 20)        .shadow(color: .black.opacity(0.8), radius: 1, x: 0, y: 1)    }}#Preview {    OnboardingMentalHealthGoalsView(coreDataViewModel: CoreDataViewModel())}